var minute = 1000 * 60;
var hour = minute * 60;
var day = hour * 24;
var halfamonth = day * 15;
var month = day * 30;
var years = month * 12
function getDate(dateTimeStamp) {
    if (dateTimeStamp == undefined) {

        return false;
    } else {
        var sTime = dateTimeStamp * 1000;//时间戳转换成正常的

        var now = new Date().getTime();//获取当前时间的时间戳

        var diffValue = now - sTime;

        if (diffValue < 0) {
            return "结束日期不能小于开始日期！"
        }
        var yearsC = diffValue / years
        var monthC = diffValue / month;
        var weekC = diffValue / (7 * day);
        var dayC = diffValue / day;
        var hourC = diffValue / hour;
        var minC = diffValue / minute;

        if (yearsC >= 1) {
            return parseInt(yearsC) + "个月前"
        } else if (monthC >= 1) {
            return parseInt(monthC) + "个月前"
        }
        else if (weekC >= 1) {
            return parseInt(weekC) + "周前"
        }
        else if (dayC >= 1) {
            return parseInt(dayC) + "天前"
        }
        else if (hourC >= 1) {
            return parseInt(hourC) + "个小时前"
        }
        else if (minC >= 1) {
            return parseInt(minC) + "分钟前"
        } else {
            return "刚刚"
        }
    }
}
export default getDate